// Function return substring (till end of string) positioned right after another substring.

const getSubstringAfterSubstring = function(str, afterSubstring) {
	let retval = '';
	const afterSubstringIndex = str.toLowerCase().indexOf(afterSubstring.toLowerCase());
	if (afterSubstringIndex !== -1) {
		retval = str.slice(afterSubstringIndex + afterSubstring.length).trim();
	}	
	return retval;
};

// Function return substring from start of a string to another substring.

const getSubstringBeforeSubstring = function(str, beforeSubstring) {
	let retval = '';
	const beforeSubstringIndex = str.toLowerCase().indexOf(beforeSubstring.toLowerCase());
	if (beforeSubstringIndex !== -1) {
		retval = str.slice(0, beforeSubstringIndex).trim();
	}	
	return retval;
};

export {
	getSubstringAfterSubstring,
	getSubstringBeforeSubstring
};